import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Form, Card, Button, Alert } from 'react-bootstrap';
import './App.css';

function App() {
  const [divisions, setDivisions] = useState([]);
  const [subdivisions, setSubdivisions] = useState([]);
  const [groups, setGroups] = useState([]);
  const [families, setFamilies] = useState([]);
  const [occupations, setOccupations] = useState([]);

  const [selectedDivision, setSelectedDivision] = useState('');
  const [selectedSubdivision, setSelectedSubdivision] = useState('');
  const [selectedGroup, setSelectedGroup] = useState('');
  const [selectedFamily, setSelectedFamily] = useState('');

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [importStatus, setImportStatus] = useState(null);

  const API_URL = 'http://localhost:5000/api';

  // Import data on first load
  useEffect(() => {
    const importData = async () => {
      try {
        setLoading(true);
        const response = await axios.post(`${API_URL}/import-data`);
        setImportStatus({ type: 'success', message: response.data.message });
      } catch (error) {
        if (error.response && error.response.status === 400) {
          // Data already imported, this is fine
          setImportStatus({ type: 'info', message: 'Using existing database data' });
        } else {
          setImportStatus({ type: 'danger', message: 'Failed to import data' });
          console.error('Error importing data:', error);
        }
      } finally {
        setLoading(false);
      }
    };

    importData();
  }, []);

  // Load divisions on component mount
  useEffect(() => {
    const fetchDivisions = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`${API_URL}/divisions`);
        setDivisions(response.data);
      } catch (error) {
        setError('Failed to fetch divisions');
        console.error('Error fetching divisions:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDivisions();
  }, []);

  // Load subdivisions when division changes
  useEffect(() => {
    if (selectedDivision) {
      const fetchSubdivisions = async () => {
        try {
          setLoading(true);
          const response = await axios.get(`${API_URL}/subdivisions/${selectedDivision}`);
          setSubdivisions(response.data);
          setSelectedSubdivision('');
          setGroups([]);
          setFamilies([]);
          setOccupations([]);
        } catch (error) {
          setError('Failed to fetch subdivisions');
          console.error('Error fetching subdivisions:', error);
        } finally {
          setLoading(false);
        }
      };

      fetchSubdivisions();
    }
  }, [selectedDivision]);

  // Load groups when subdivision changes
  useEffect(() => {
    if (selectedSubdivision) {
      const fetchGroups = async () => {
        try {
          setLoading(true);
          const response = await axios.get(`${API_URL}/groups/${selectedSubdivision}`);
          setGroups(response.data);
          setSelectedGroup('');
          setFamilies([]);
          setOccupations([]);
        } catch (error) {
          setError('Failed to fetch groups');
          console.error('Error fetching groups:', error);
        } finally {
          setLoading(false);
        }
      };

      fetchGroups();
    }
  }, [selectedSubdivision]);

  // Load families when group changes
  useEffect(() => {
    if (selectedGroup) {
      const fetchFamilies = async () => {
        try {
          setLoading(true);
          const response = await axios.get(`${API_URL}/families/${selectedGroup}`);
          setFamilies(response.data);
          setSelectedFamily('');
          setOccupations([]);
        } catch (error) {
          setError('Failed to fetch families');
          console.error('Error fetching families:', error);
        } finally {
          setLoading(false);
        }
      };

      fetchFamilies();
    }
  }, [selectedGroup]);

  // Load occupations when family changes
  useEffect(() => {
    if (selectedFamily) {
      const fetchOccupations = async () => {
        try {
          setLoading(true);
          const response = await axios.get(`${API_URL}/occupations`);
          const filteredOccupations = response.data
            .filter(item => item.Family_Title === selectedFamily)
            .flatMap(item => item.Occupations);
          setOccupations(filteredOccupations);
        } catch (error) {
          setError('Failed to fetch occupations');
          console.error('Error fetching occupations:', error);
        } finally {
          setLoading(false);
        }
      };

      fetchOccupations();
    }
  }, [selectedFamily]);

  return (
    <Container className="py-5">
      <Card className="shadow-sm mb-5">
        <Card.Header className="bg-primary text-white">
          <h1 className="text-center">Government Occupational Database</h1>
        </Card.Header>
        <Card.Body>
          {importStatus && (
            <Alert variant={importStatus.type} dismissible>
              {importStatus.message}
            </Alert>
          )}

          {error && (
            <Alert variant="danger" dismissible onClose={() => setError(null)}>
              {error}
            </Alert>
          )}

          <Row className="mb-4">
            <Col>
              <Form.Group>
                <Form.Label>Division</Form.Label>
                <Form.Control 
                  as="select" 
                  value={selectedDivision}
                  onChange={(e) => setSelectedDivision(e.target.value)}
                  disabled={loading || divisions.length === 0}
                >
                  <option value="">Select Division</option>
                  {divisions.map((division, index) => (
                    <option key={index} value={division}>{division}</option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Col>

            <Col>
              <Form.Group>
                <Form.Label>Sub-Division</Form.Label>
                <Form.Control 
                  as="select" 
                  value={selectedSubdivision}
                  onChange={(e) => setSelectedSubdivision(e.target.value)}
                  disabled={loading || !selectedDivision || subdivisions.length === 0}
                >
                  <option value="">Select Sub-Division</option>
                  {subdivisions.map((subdivision, index) => (
                    <option key={index} value={subdivision}>{subdivision}</option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Col>
          </Row>

          <Row className="mb-4">
            <Col>
              <Form.Group>
                <Form.Label>Group</Form.Label>
                <Form.Control 
                  as="select" 
                  value={selectedGroup}
                  onChange={(e) => setSelectedGroup(e.target.value)}
                  disabled={loading || !selectedSubdivision || groups.length === 0}
                >
                  <option value="">Select Group</option>
                  {groups.map((group, index) => (
                    <option key={index} value={group}>{group}</option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Col>

            <Col>
              <Form.Group>
                <Form.Label>Family</Form.Label>
                <Form.Control 
                  as="select" 
                  value={selectedFamily}
                  onChange={(e) => setSelectedFamily(e.target.value)}
                  disabled={loading || !selectedGroup || families.length === 0}
                >
                  <option value="">Select Family</option>
                  {families.map((family, index) => (
                    <option key={index} value={family}>{family}</option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Col>
          </Row>

          {occupations.length > 0 && (
            <div className="mt-4">
              <h3>Occupations</h3>
              <div className="table-responsive">
                <table className="table table-striped table-bordered">
                  <thead className="thead-dark">
                    <tr>
                      <th>Code</th>
                      <th>Title</th>
                      <th>NCO 2004 Code</th>
                    </tr>
                  </thead>
                  <tbody>
                    {occupations.map((occupation, index) => (
                      <tr key={index}>
                        <td>{occupation.Code}</td>
                        <td>{occupation.Title}</td>
                        <td>{occupation.NCO_2004_Code}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </Card.Body>
        <Card.Footer className="text-center text-muted">
          Government Occupational Database Â© {new Date().getFullYear()}
        </Card.Footer>
      </Card>
    </Container>
  );
}

export default App;
