import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Card, Alert } from 'react-bootstrap';
import './App.css';
import Login from './Login';

const TreeNode = ({ label, children, isExpanded, onToggle }) => {
  return (
    <div className="tree-node">
      <div className="tree-content" onClick={onToggle}>
        {children && (
          <svg
            className={`chevron ${isExpanded ? 'expanded' : ''}`}
            viewBox="0 0 24 24"
            fill="none"
            stroke="currentColor"
          >
            <path
              d="M9 18l6-6-6-6"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
          </svg>
        )}
        <span className="tree-node-content">{label}</span>
      </div>
      {children && (
        <div className={`tree-children ${isExpanded ? 'expanded' : ''}`}>
          {children}
        </div>
      )}
    </div>
  );
};

function App() {
  const [hierarchyData, setHierarchyData] = useState({});
  const [expandedNodes, setExpandedNodes] = useState(new Set());
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [importStatus, setImportStatus] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [showLoginForm, setShowLoginForm] = useState(false);

  const handleLogin = (data) => {
    setIsLoggedIn(true);
    setIsAdmin(data.isAdmin);
    setShowLoginForm(false);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('isAdmin');
    setIsLoggedIn(false);
    setIsAdmin(false);
  };

  const renderTree = (data, path = '') => {
    if (Array.isArray(data)) {
      return (
        <div className="table-responsive mt-2">
          <table className="table table-striped table-bordered">
            <thead className="thead-dark">
              <tr>
                <th>Code</th>
                <th>Title</th>
                <th>NCO 2004 Code</th>
              </tr>
            </thead>
            <tbody>
              {data.map((occupation, index) => (
                <tr key={index}>
                  <td>{occupation.Code}</td>
                  <td>{occupation.Title}</td>
                  <td>{occupation.NCO_2004_Code}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }

    return Object.entries(data).map(([key, value]) => {
      const currentPath = path ? `${path}-${key}` : key;
      const isExpanded = expandedNodes.has(currentPath);

      return (
        <TreeNode
          key={currentPath}
          label={key}
          isExpanded={isExpanded}
          onToggle={() => toggleNode(currentPath)}
        >
          {isExpanded && renderTree(value, currentPath)}
        </TreeNode>
      );
    });
  };

  return (
    <Container className="py-5">
      <div className="admin-controls">
        {!isLoggedIn ? (
          <Button variant="outline-primary" onClick={() => setShowLoginForm(true)}>
            Login
          </Button>
        ) : (
          <Button variant="outline-danger" onClick={handleLogout}>
            Logout
          </Button>
        )}
      </div>

      {showLoginForm && (
        <Login onLogin={handleLogin} />
      )}

      <Card className="shadow-sm mb-5">
        <Card.Header className="bg-primary text-white">
          <h1 className="text-center">Government Occupational Database</h1>
        </Card.Header>
        <Card.Body>
          {importStatus && (
            <Alert variant={importStatus.type} dismissible>
              {importStatus.message}
            </Alert>
          )}

          {error && (
            <Alert variant="danger" dismissible onClose={() => setError(null)}>
              {error}
            </Alert>
          )}

          {loading ? (
            <div className="text-center">Loading...</div>
          ) : (
            <div className="tree-view">
              {renderTree(hierarchyData)}
            </div>
          )}
        </Card.Body>
        <Card.Footer className="text-center text-muted">
          Government Occupational Database © {new Date().getFullYear()}
        </Card.Footer>
      </Card>
    </Container>
  );
}

export default App;
